class DropdownMenus{constructor(){var self=this;window.shown_dropdown=null;$("ul.dropdown > li").click(function(ev){if(window.shown_dropdown){if(ev.target.tagName!=="SELECT"&&ev.target.tagName!=="INPUT"){self.hide_all()}}else{var dropdown=$("ul:first",this);if(dropdown.length>0){dropdown.css("max-height","1000px");dropdown.css("border-width","1px");window.shown_dropdown=dropdown.get(0);ev.stopPropagation()}}});$("html").click(function(e){if(window.shown_dropdown&&!window.shown_dropdown.contains(e.target)){window.shown_dropdown.style["max-height"]=0;window.shown_dropdown.style["border-width"]=0;window.shown_dropdown=null}})}hide_all(){if(window.shown_dropdown){window.shown_dropdown.style["max-height"]=0;window.shown_dropdown.style["border-width"]=0;window.shown_dropdown=null}}}